import React from 'react';
import { motion } from 'framer-motion';

const Experience = () => {
  const experiences = [
    {
      company: "Artxtic",
      position: "Frontend Web Developer",
      period: "2023-Present",
      description: [
        "Built an AI SaaS platform for ecommerce product image generation",
        "Implemented responsive UI components with React",
        "Optimized performance for high-volume image processing"
      ],
      projectLink: "artxtic.com"
    }
  ];

  return (
    <section id="experience" className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center mb-16">#Experience</h2>

        <div className="max-w-4xl mx-auto relative">
          {/* Timeline line */}
          <div className="absolute left-4 top-0 bottom-0 w-1 bg-orange-500"></div>

          {experiences.map((exp, index) => (
            <motion.div 
              key={index}
              className="ml-16 mb-16 relative"
              initial={{ opacity: 0, x: -20 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.2 }}
            >
              {/* Timeline dot */}
              <div className="absolute w-6 h-6 bg-orange-500 rounded-full -left-10 top-4 border-4 border-white"></div>

              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-2xl font-bold mb-2">{exp.company}</h3>
                <div className="flex flex-col md:flex-row md:items-center mb-4">
                  <span className="font-semibold text-orange-600">{exp.position}</span>
                  <span className="md:ml-4 text-gray-600">| {exp.period}</span>
                </div>

                <ul className="mb-6 space-y-2">
                  {exp.description.map((item, i) => (
                    <li key={i} className="flex items-start">
                      <span className="text-orange-500 mr-2">•</span>
                      <span>{item}</span>
                    </li>
                  ))}
                </ul>

                <motion.a 
                  href={`https://${exp.projectLink}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-flex items-center text-orange-500 font-medium"
                  whileHover={{ x: 5 }}
                >
                  View Project →
                  <span className="ml-2">{exp.projectLink}</span>
                </motion.a>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Experience;
