import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { FaGithub, FaLinkedin, FaTwitter, FaEnvelope } from 'react-icons/fa';

const Hero = () => {
  const [text, setText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed] = useState(150);

  const texts = [
    'Machine Learning Engineer',
    'Deep Learning Specialist',
    'AI Researcher',
    'Data Scientist',
    'MLOps Enthusiast'
  ];

  useEffect(() => {
    const handleTyping = () => {
      const currentText = texts[loopNum % texts.length];

      setText(isDeleting 
        ? currentText.substring(0, text.length - 1)
        : currentText.substring(0, text.length + 1)
      );

      if (!isDeleting && text === currentText) {
        setTimeout(() => setIsDeleting(true), 1500);
      } else if (isDeleting && text === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleTyping, isDeleting ? 100 : typingSpeed);
    return () => clearTimeout(timer);
  }, [text, isDeleting, loopNum, typingSpeed, texts]);

  const socialLinks = [
    { 
      name: 'GitHub', 
      icon: <FaGithub className="w-6 h-6" />, 
      url: 'https://github.com/yourusername',
      color: 'hover:text-green-400'
    },
    { 
      name: 'LinkedIn', 
      icon: <FaLinkedin className="w-6 h-6" />, 
      url: 'https://linkedin.com/in/yourusername',
      color: 'hover:text-green-400'
    },
    { 
      name: 'Twitter', 
      icon: <FaTwitter className="w-6 h-6" />, 
      url: 'https://twitter.com/yourusername',
      color: 'hover:text-green-400'
    },
    { 
      name: 'Email', 
      icon: <FaEnvelope className="w-6 h-6" />, 
      url: 'mailto:your.email@example.com',
      color: 'hover:text-green-400'
    }
  ];

  return (
    <section id="home" className="relative min-h-screen flex items-center justify-center pt-20 bg-gray-900">
      {/* Background */}
      <div className="absolute inset-0 bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900">
        <div className="absolute inset-0 opacity-10 bg-[url('/grid.svg')]"></div>
      </div>

      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-16 relative z-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          {/* Left Column - Text Content */}
          <motion.div 
            className="text-center lg:text-left"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <motion.h1 
              className="text-4xl md:text-5xl lg:text-6xl font-bold mb-6 text-white"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              Hi, I'm <span className="text-green-400">Your Name</span>
            </motion.h1>

            <motion.div 
              className="text-2xl md:text-3xl font-medium mb-8 text-gray-300 h-12"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              {text}
              <span className="ml-1 animate-pulse">|</span>
            </motion.div>

            <motion.p 
              className="text-lg md:text-xl text-gray-400 mb-10 max-w-lg mx-auto lg:mx-0"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.6 }}
            >
              I build intelligent systems and solve complex problems with machine learning and deep learning.
            </motion.p>

            <motion.div 
              className="flex flex-wrap justify-center lg:justify-start gap-4 mb-12"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.8 }}
            >
              <a 
                href="#contact" 
                className="px-8 py-3 bg-gradient-to-r from-green-500 to-green-600 text-white rounded-lg font-medium hover:opacity-90 transition-opacity"
              >
                Contact Me
              </a>
              <a 
                href="/resume.pdf" 
                target="_blank" 
                rel="noopener noreferrer"
                className="px-8 py-3 border-2 border-green-500 text-green-400 rounded-lg font-medium hover:bg-green-900/30 transition-colors"
              >
                View Resume
              </a>
            </motion.div>

            <motion.div 
              className="flex justify-center lg:justify-start space-x-6"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 1 }}
            >
              {socialLinks.map((link) => (
                <a
                  key={link.name}
                  href={link.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={`text-gray-400 ${link.color} transition-colors`}
                  aria-label={link.name}
                >
                  {link.icon}
                </a>
              ))}
            </motion.div>
          </motion.div>

          {/* Right Column - Glass Card */}
          <motion.div 
            className="w-full lg:w-1/2 mt-8 lg:mt-0"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7, delay: 0.4, ease: "easeOut" }}
          >
            <motion.div 
              className="relative p-8 rounded-2xl overflow-hidden h-full"
              whileHover={{ y: -5 }}
              initial={{ opacity: 0, scale: 0.98 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: 0.5 }}
            >
              {/* Glass Background */}
              <div className="absolute inset-0 bg-gray-800/30 backdrop-blur-lg rounded-2xl border border-gray-700/50"></div>

              {/* Content */}
              <div className="relative z-10">
                <p className="text-gray-300 mb-4 leading-relaxed text-lg">
                  I'm a passionate <span className="text-green-400 font-semibold">Machine Learning Engineer</span> and{' '}
                  <span className="text-green-400 font-semibold">Full-Stack Developer</span> with expertise in building intelligent systems and scalable web applications.
                </p>
                <p className="text-gray-400 leading-relaxed">
                  My journey in tech has led me to specialize in AI/ML solutions that solve real-world problems through innovative thinking and clean, efficient code.
                </p>
              </div>
            </motion.div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
