import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const Navbar = () => {
  const [activeSection, setActiveSection] = useState('home');

  useEffect(() => {
    const handleScroll = () => {
      const sections = ['home', 'about', 'skills', 'experience', 'projects'];
      const currentSection = sections.find(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          return rect.top <= 100 && rect.bottom >= 100;
        }
        return false;
      });

      if (currentSection) setActiveSection(currentSection);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navItems = [
    { id: 'home', label: 'Home' },
    { id: 'about', label: 'AboutMe' },
    { id: 'skills', label: 'Skills' },
    { id: 'experience', label: 'Experience' },
    { id: 'projects', label: 'Projects' },
  ];

  return (
    <header className="sticky top-0 z-50 bg-white shadow-md py-4 px-8">
      <div className="container mx-auto flex justify-between items-center">
        <div className="font-bold text-xl">Abhishek Borkar</div>

        <nav className="hidden md:flex space-x-8">
          {navItems.map((item) => (
            <a 
              key={item.id}
              href={`#${item.id}`}
              className={`relative px-1 ${activeSection === item.id ? 'text-orange-500' : 'text-gray-800'}`}
            >
              {item.label}
              {activeSection === item.id && (
                <motion.div 
                  className="absolute bottom-0 left-0 w-full h-0.5 bg-orange-500"
                  layoutId="underline"
                />
              )}
            </a>
          ))}
        </nav>

        <motion.button
          whileHover={{ 
            scale: 1.05,
            boxShadow: "0 0 15px #FF6A00, 0 0 30px #FF6A00"
          }}
          className="bg-orange-500 text-white px-6 py-2 rounded-md font-medium"
        >
          Hire Me
        </motion.button>
      </div>
    </header>
  );
};

export default Navbar;
