import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';

const Navbar = () => {
  const [activeSection, setActiveSection] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  const scrollTimeout = useRef(null);

  // Handle smooth scroll to section
  const scrollToSection = (e, sectionId) => {
    e.preventDefault();
    const element = document.getElementById(sectionId);
    if (element) {
      window.scrollTo({
        top: element.offsetTop - 80, // Offset for fixed header
        behavior: 'smooth'
      });
    }
  };

  // Update active section on scroll with debounce
  useEffect(() => {
    const handleScroll = () => {
      // Set scrolled state for navbar background
      setScrolled(window.scrollY > 50);

      // Debounce the scroll handler
      if (scrollTimeout.current) {
        cancelAnimationFrame(scrollTimeout.current);
      }

      scrollTimeout.current = requestAnimationFrame(() => {
        const sections = ['home', 'about', 'skills', 'experience', 'projects'];
        const scrollPosition = window.scrollY + 100; // Add offset to highlight section a bit earlier

        for (const section of sections) {
          const element = document.getElementById(section);
          if (element) {
            const offsetTop = element.offsetTop;
            const offsetHeight = element.offsetHeight;

            if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
              setActiveSection(section);
              break;
            }
          }
        }
      });
    };

    // Add scroll event listener
    window.addEventListener('scroll', handleScroll, { passive: true });

    // Initial check
    handleScroll();

    // Cleanup
    return () => {
      if (scrollTimeout.current) {
        cancelAnimationFrame(scrollTimeout.current);
      }
      window.removeEventListener('scroll', handleScroll, { passive: true });
    };
  }, []);

  const navItems = [
    { id: 'home', label: 'Home' },
    { id: 'about', label: 'AboutMe' },
    { id: 'skills', label: 'Skills' },
    { id: 'experience', label: 'Experience' },
    { id: 'projects', label: 'Projects' },
  ];

  return (
    <header className={`fixed top-6 left-1/2 -translate-x-1/2 z-50 transition-all duration-300 w-[90%] max-w-4xl mx-auto ${
      scrolled ? 'py-3' : 'py-2'
    }`}>
      <div className={`glass-card container mx-auto px-6 py-2 rounded-2xl ${
        scrolled 
          ? 'bg-white/80 border border-white/30 shadow-lg' 
          : 'bg-white/60'
      } transition-all duration-300`}>
        <div className="flex justify-between items-center">
          {/* Logo/Name on the left */}
          <div className="flex items-center">
            <span className="text-xl font-bold bg-gradient-to-r from-golden-600 to-red-500 bg-clip-text text-transparent">
              HP
            </span>
          </div>

          {/* Navigation links */}
          <nav className="hidden md:flex items-center space-x-8">
            {navItems.map((item) => (
              <a 
                key={item.id}
                href={`#${item.id}`}
                onClick={(e) => scrollToSection(e, item.id)}
                className={`relative px-1 text-sm font-medium ${
                  activeSection === item.id ? 'text-white' : 'text-gray-400 hover:text-white transition-colors'
                }`}
              >
                {activeSection === item.id ? (
                  <>
                    <span className="text-white">&lt;/{item.label}&gt;</span>
                    <span className="absolute -left-2 top-1/2 transform -translate-y-1/2 w-1.5 h-1.5 bg-gradient-to-r from-pink-500 to-purple-600 rounded-full"></span>
                  </>
                ) : (
                  <span>&lt;/{item.label}&gt;</span>
                )}
              </a>
            ))}
          </nav>

          {/* Mobile menu button (hidden for now) */}
          <button className="md:hidden text-white">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
