import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { FiArrowLeft, FiExternalLink, FiGithub } from 'react-icons/fi';
import { FaFigma } from 'react-icons/fa';
import Fireflies from './Fireflies';
import Navbar from './Navbar';
import Footer from './Footer';

const MarketingLabCaseStudy = () => {
  const navigate = useNavigate();

  // Impact stats
  const stats = [
    { 
      value: 'Enhanced',
      label: 'User Engagement',
      description: 'Significantly improved user interaction and time spent on platform'
    },
    { 
      value: 'Streamlined',
      label: 'Conversion Funnel',
      description: 'Optimized user journey leading to better conversion rates'
    },
    { 
      value: 'Improved',
      label: 'User Satisfaction',
      description: 'Received positive feedback on the intuitive interface and features'
    },
  ];

  const features = [
    {
      title: '3D Data Visualization',
      description: 'Interactive Three.js powered data representations with cyberpunk styling.',
      placeholder: '3D Visualization'
    },
    {
      title: 'Dynamic Forms',
      description: 'Context-aware form fields that adapt based on user inputs and selections.',
      placeholder: 'Form Screenshot'
    },
    {
      title: 'Real-time Analytics',
      description: 'Live campaign performance metrics with interactive filtering options.',
      placeholder: 'Analytics Screenshot'
    }
  ];

  const designProcess = [
    { title: 'Initial Wireframes', placeholder: 'Wireframe' },
    { title: 'Design System', placeholder: 'Style Guide' },
    { title: 'Interactive Prototype', placeholder: 'Prototype' }
  ];

  // Smooth scroll to top on component mount
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 relative overflow-hidden">
      <Fireflies count={20} />
      <Navbar />

      {/* Back Button */}
      <motion.button
        onClick={() => navigate(-1)}
        className="fixed top-20 left-6 z-50 flex items-center text-green-400 hover:text-green-300 transition-colors bg-gray-800/90 hover:bg-gray-700 px-4 py-2 rounded-lg border border-gray-700 shadow-md"
        initial={{ opacity: 0, y: -10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.3 }}
        whileHover={{ x: 5 }}
      >
        <FiArrowLeft className="mr-2" /> Back to Portfolio
      </motion.button>

      {/* Hero Section */}
      <section className="relative pt-32 pb-16 md:pt-40 md:pb-24 overflow-hidden bg-gray-900 text-gray-100">
        <div className="absolute inset-0 overflow-hidden opacity-20">
          <div className="absolute top-0 right-0 w-64 h-64 bg-green-600 rounded-full mix-blend-multiply filter blur-xl opacity-20"></div>
          <div className="absolute bottom-0 left-0 w-64 h-64 bg-green-700 rounded-full mix-blend-multiply filter blur-xl opacity-20"></div>
        </div>
        <div className="container mx-auto px-4 text-center relative">
          <motion.h1 
            className="text-4xl md:text-5xl font-bold mb-6 text-white leading-tight"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Marketing Lab
          </motion.h1>
          <div className="w-24 h-1 bg-gradient-to-r from-green-400 to-green-600 rounded-full mx-auto mb-8"></div>
          <motion.p 
            className="text-lg text-gray-300 max-w-3xl mx-auto leading-relaxed"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2, duration: 0.6 }}
          >
            Interactive Marketing Campaign Simulator
          </motion.p>
        </div>
      </section>

      {/* Demo & Architecture Section */}
      <section className="py-12 bg-gray-900">
        <div className="container mx-auto px-4">
          <motion.h2 
            className="text-3xl font-bold mb-8 text-center text-white"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, margin: "-50px" }}
            transition={{ duration: 0.6 }}
          >
            Project Demo & Architecture
          </motion.h2>

          {/* Demo Video */}
          <div className="mb-12 max-w-7xl mx-auto px-4">
            <h3 className="text-xl font-semibold mb-4 text-green-400">Interactive Demo</h3>
            <div className="w-full bg-gray-800 rounded-xl overflow-hidden border-2 border-dashed border-gray-700 flex items-center justify-center" style={{ height: '350px' }}>
              <div className="text-center p-6">
                <p className="text-gray-400 mb-2">[Demo Video GIF will be displayed here]</p>
                <p className="text-sm text-gray-500">Replace with your demo-video.gif in /public/gifs/</p>
              </div>
            </div>
            <p className="mt-3 text-sm text-gray-300 text-center max-w-2xl mx-auto">
              A walkthrough of the Marketing Lab interface showing key features and user flows.
            </p>
          </div>

          {/* Architecture Design */}
          <div className="max-w-7xl mx-auto px-4">
            <h3 className="text-xl font-semibold mb-4 text-green-400">System Architecture</h3>
            <div className="w-full bg-gray-800 rounded-xl overflow-hidden border-2 border-dashed border-gray-700 flex items-center justify-center" style={{ height: '350px' }}>
              <div className="text-center p-6">
                <p className="text-gray-400 mb-2">[Architecture Diagram will be displayed here]</p>
                <p className="text-sm text-gray-500">Replace with your architecture.gif in /public/gifs/</p>
              </div>
            </div>
            <p className="mt-3 text-sm text-gray-300 text-center max-w-2xl mx-auto">
              High-level overview of the system architecture and data flow.
            </p>
          </div>
        </div>
      </section>

      {/* Overview Section */}
      <section className="py-16 bg-gray-900">
        <div className="container mx-auto px-4">
          <motion.div 
            className="max-w-4xl mx-auto"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
          >
            <h2 className="text-3xl font-bold mb-6 text-center text-white">Project Overview</h2>
            <div className="prose prose-lg text-gray-300 max-w-none prose-headings:text-white prose-strong:text-white prose-a:text-green-400 hover:prose-a:text-green-300">
              Marketing Lab is an interactive marketing campaign simulator that combines data visualization with a cyberpunk aesthetic. 
              The application helps marketers plan, simulate, and analyze marketing campaigns through an engaging, futuristic interface.
            </div>

            <div className="grid md:grid-cols-2 gap-6 mb-12">
              <div className="bg-gray-800 rounded-xl p-6 border border-gray-700 shadow-sm hover:shadow-md transition-shadow">
                <h3 className="text-xl font-semibold mb-3 text-white">Challenge</h3>
                <p className="text-gray-300 leading-relaxed">Create an intuitive yet powerful tool for marketers to visualize and optimize campaigns with complex data in an accessible way.</p>
              </div>
              <div className="bg-gray-800 rounded-xl p-6 border border-gray-700 shadow-sm hover:shadow-md transition-shadow">
                <h3 className="text-xl font-semibold mb-3 text-white">Solution</h3>
                <p className="text-gray-300 leading-relaxed">A responsive web application with 3D data visualization, real-time analytics, and an intuitive interface that makes complex data approachable.</p>
              </div>
            </div>

            <div className="grid md:grid-cols-2 gap-6">
              <div className="bg-gray-800 p-6 rounded-xl border border-gray-700 shadow-sm">
                <h3 className="text-xl font-semibold mb-4 text-white">Technologies</h3>
                <ul className="space-y-2 text-gray-300">
                  <li className="flex items-center">
                    <span className="w-1.5 h-1.5 bg-green-500 rounded-full mr-2"></span>
                    React & Three.js
                  </li>
                  <li className="flex items-center">
                    <span className="w-1.5 h-1.5 bg-green-500 rounded-full mr-2"></span>
                    Framer Motion
                  </li>
                  <li className="flex items-center">
                    <span className="w-1.5 h-1.5 bg-green-500 rounded-full mr-2"></span>
                    Tailwind CSS
                  </li>
                  <li className="flex items-center">
                    <span className="w-1.5 h-1.5 bg-green-500 rounded-full mr-2"></span>
                    Node.js & Express
                  </li>
                </ul>
              </div>
              <div className="bg-gray-800 p-6 rounded-xl border border-gray-700 shadow-sm">
                <h3 className="text-xl font-semibold mb-4 text-white">My Role</h3>
                <p className="mb-4 text-gray-300 leading-relaxed">UI/UX Design, Frontend Development, 3D Visualization</p>
                <div className="flex flex-wrap gap-2">
                  <span className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm">UI Design</span>
                  <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">3D Graphics</span>
                  <span className="px-3 py-1 bg-purple-100 text-purple-800 rounded-full text-sm">Prototyping</span>
                </div>
              </div>
            </div>
          </motion.div>
        </div>
