import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section id="home" className="min-h-screen flex items-center relative overflow-hidden">
      {/* Particle Background */}
      <div className="absolute inset-0 z-0">
        <div className="absolute top-1/4 left-1/4 w-4 h-4 bg-orange-500 rounded-full animate-pulse"></div>
        <div className="absolute top-1/3 right-1/3 w-3 h-3 bg-orange-500 rounded-full animate-pulse"></div>
        <div className="absolute bottom-1/4 left-2/5 w-5 h-5 bg-orange-500 rounded-full animate-pulse"></div>
      </div>

      <div className="container mx-auto px-4 py-16 relative z-10 grid grid-cols-1 md:grid-cols-2 gap-12">
        <div className="flex flex-col justify-center">
          <div className="text-2xl font-mono mb-4">
            <span className="typewriter block">Namaste();</span>
          </div>
          <h1 className="text-5xl md:text-6xl font-bold mb-4 font-orbitron">
            I'm Abhishek Borkar.
          </h1>
          <h2 className="text-3xl md:text-4xl font-light mb-8">
            I develop websites.
          </h2>
          <motion.button
            whileHover={{ scale: 1.05 }}
            className="self-start bg-orange-500 text-white px-8 py-3 rounded-md font-medium text-lg"
          >
            View Projects
          </motion.button>
        </div>

        <div className="flex justify-center items-center">
          <div className="relative w-64 h-64 md:w-80 md:h-80 bg-gray-200 rounded-full flex items-center justify-center">
            {/* 3D Avatar Placeholder */}
            <div className="text-center">
              <div className="text-4xl mb-2">üë®‚Äçüíª</div>
              <p className="text-gray-600">3D Avatar</p>
            </div>

            {/* Floating Tech Orbs */}
            {['React', 'TypeScript', 'JavaScript', 'Node.js'].map((tech, idx) => (
              <motion.div
                key={tech}
                className="absolute w-12 h-12 bg-white rounded-full flex items-center justify-center shadow-lg"
                animate={{
                  rotate: 360,
                  x: [0, 50 * Math.sin(idx * Math.PI / 2), 0],
                  y: [0, 50 * Math.cos(idx * Math.PI / 2), 0]
                }}
                transition={{
                  duration: 8,
                  repeat: Infinity,
                  ease: "linear"
                }}
              >
                <span className="text-xs font-bold">{tech}</span>
              </motion.div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
