import React, { useEffect, useState } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { FaGithub, FaLinkedin, FaTwitter, FaEnvelope } from 'react-icons/fa';

// Fire text effect component
const FireText = ({ text }) => {
  return (
    <span className="relative inline-block">
      <span className="absolute inset-0 bg-gradient-to-r from-yellow-400 via-red-500 to-red-700 bg-clip-text text-transparent animate-gradient-x">
        {text}
      </span>
      <span className="relative z-10 text-transparent bg-clip-text bg-gradient-to-r from-yellow-200 via-yellow-100 to-yellow-300">
        {text}
      </span>
    </span>
  );
};

const Hero = () => {
  const [text, setText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);

  const texts = [
    'Frontend Developer',
    'React Specialist',
    'UI/UX Enthusiast',
    'Problem Solver',
    'Machine Learning Enthusiast'
  ];

  const handleTyping = () => {
    const currentText = texts[loopNum % texts.length];

    if (isDeleting) {
      // Delete text
      setText(currentText.substring(0, text.length - 1));
      setTypingSpeed(75);
    } else {
      // Type text
      setText(currentText.substring(0, text.length + 1));
      setTypingSpeed(150);
    }
  };

  useEffect(() => {
    let timer;
    const currentText = texts[loopNum % texts.length];

    if (!isDeleting && text === currentText) {
      // Pause at end of typing
      timer = setTimeout(() => setIsDeleting(true), 2000);
    } else if (isDeleting && text === '') {
      // Move to next text after deleting
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
    } else {
      timer = setTimeout(handleTyping, typingSpeed);
    }

    return () => clearTimeout(timer);
  }, [text, isDeleting, loopNum, typingSpeed, texts]);

  const socialLinks = [
    { 
      name: 'GitHub', 
      icon: (
        <FaGithub className="w-6 h-6" />
      ),
      url: 'https://github.com/abhishek' 
    },
    { 
      name: 'LinkedIn', 
      icon: (
        <FaLinkedin className="w-6 h-6" />
      ),
      url: '#' 
    },
    { 
      name: 'Twitter', 
      icon: (
        <FaTwitter className="w-6 h-6" />
      ),
      url: '#' 
    },
    { 
      name: 'Email', 
      icon: (
        <FaEnvelope className="w-6 h-6" />
      ),
      url: 'mailto:abhishek@example.com' 
    }
  ];

  return (
    <section 
      id="home" 
      className="min-h-screen flex items-center justify-center relative bg-black text-white overflow-hidden pt-20 md:pt-24"
    >
      {/* Black Hole Background */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-black via-gray-900 to-gray-800"></div>
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,rgba(16,185,129,0.05),transparent_70%)]"></div>
        <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-10"></div>

        {/* Black Hole Core */}
        <motion.div 
          className="absolute top-1/2 left-1/2 w-0 h-0 rounded-full"
          style={{
            boxShadow: '0 0 200px 100px rgba(16, 185, 129, 0.1)',
            transform: 'translate(-50%, -50%)',
          }}
          animate={{
            boxShadow: [
              '0 0 200px 100px rgba(16, 185, 129, 0.1)',
              '0 0 250px 125px rgba(16, 185, 129, 0.05)',
              '0 0 200px 100px rgba(16, 185, 129, 0.1)',
            ]
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            repeatType: 'reverse'
          }}
        />

        {/* Particle Effects */}
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute rounded-full bg-green-400"
            style={{
              width: Math.random() * 6 + 2 + 'px',
              height: Math.random() * 6 + 2 + 'px',
              left: Math.random() * 100 + '%',
              top: Math.random() * 100 + '%',
              opacity: Math.random() * 0.5 + 0.1,
            }}
            animate={{
              x: [0, (Math.random() - 0.5) * 100],
              y: [0, (Math.random() - 0.5) * 100],
              opacity: [0.1, 0.6, 0.1],
            }}
            transition={{
              duration: Math.random() * 10 + 10,
              repeat: Infinity,
              repeatType: 'reverse',
              ease: 'easeInOut',
            }}
          />
        ))}
      </div>

      <div className="w-full px-4 py-8 md:py-16 relative z-10 mt-16">
        <div className="max-w-4xl w-[90%] mx-auto text-left">
          {/* Greeting */}
          <motion.div 
            className="text-xl md:text-2xl text-green-400 mb-4 font-mono"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            &lt;Hello World!&gt; I'm
          </motion.div>

          {/* Name */}
          <motion.h1 
            className="text-5xl md:text-7xl font-bold mb-6 bg-clip-text text-transparent bg-gradient-to-r from-yellow-200 via-yellow-100 to-yellow-300"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
          >
            <FireText text="HARSHIT PANDEY" />
          </motion.h1>

          {/* Typewriter Effect */}
          <motion.div
            className="text-2xl md:text-4xl font-bold mb-8 h-12 md:h-16 flex items-start w-full"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <div className="w-full">
              <span className="text-gray-700">I'm a </span>
              <span className="text-red-500 border-r-2 border-red-400 animate-pulse">
                {text}
              </span>
            </div>
          </motion.div>

          {/* Introduction */}
          <motion.div 
            className="glass-card p-8 rounded-2xl w-full max-w-2xl mb-12 bg-black/40 backdrop-blur-sm border border-green-900/50"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
            animate={controls}
          >
            <p className="text-lg md:text-xl text-black-700 mb-4">
              I build exceptional digital experiences with modern web technologies. 
            </p>
            <p className="text-lg md:text-xl text-black-700">
              Currently focused on creating beautiful, responsive, and user-friendly applications.
            </p>
          </motion.div>

          {/* CTA Buttons */}
          <motion.div 
            className="flex flex-wrap gap-6 mb-16"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.4 }}
          >
            <a 
              href="#contact" 
              className="glass-button px-8 py-4 bg-gradient-to-r from-green-600 to-green-700 text-white font-medium rounded-xl hover:shadow-lg transition-all duration-300 hover:scale-105 transform"
            >
              Get In Touch
            </a>
            <a 
              href="#about" 
              className="glass-button px-8 py-4 bg-white/80 text-black-800 font-medium rounded-xl hover:shadow-lg transition-all duration-300 hover:scale-105 transform border-2 border-green-300"
            >
              Learn More
            </a>
          </motion.div>


        </div>

        {/* Avatar section removed */}
      </div>

      {/* Back to top link - Same as other sections */}
      <div className="fixed right-10 top-1/2 transform -translate-y-1/2 rotate-90 origin-right text-sm text-gray-500 hover:text-gray-800 transition-colors z-50">
        <a href="#" className="flex items-center">
          BACK TO TOP
        </a>
      </div>
    </section>
  );
};

export default Hero;
