import React, { useState, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const TechStack = () => {
  const techData = [
    { name: 'React', proficiency: 90, logo: '‚öõÔ∏è' },
    { name: 'TypeScript', proficiency: 85, logo: 'üìù' },
    { name: 'JavaScript', proficiency: 95, logo: 'üìú' },
    { name: 'Node.js', proficiency: 80, logo: 'üü¢' },
  ];

  return (
    <section id="skills" className="py-20 bg-white">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-bold text-center mb-16">#Tech Stack</h2>

        <div className="max-w-3xl mx-auto space-y-10">
          {techData.map((tech, index) => (
            <TechItem 
              key={index}
              tech={tech}
              index={index}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

const TechItem = ({ tech, index }) => {
  const controls = useAnimation();
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  useEffect(() => {
    if (inView) {
      controls.start({
        width: `${tech.proficiency}%`,
        transition: { duration: 1.5, ease: "easeOut" }
      });
    }
  }, [controls, inView, tech.proficiency]);

  return (
    <motion.div 
      ref={ref}
      className="group"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, delay: index * 0.2 }}
      whileHover={{ scale: 1.02 }}
    >
      <div className="flex items-center mb-2">
        <span className="text-2xl mr-3">{tech.logo}</span>
        <h3 className="text-xl font-bold">{tech.name}</h3>
        <span className="ml-auto text-gray-600">{tech.proficiency}%</span>
      </div>

      <div className="h-3 bg-gray-200 rounded-full overflow-hidden">
        <motion.div
          className="h-full bg-orange-500 rounded-full"
          initial={{ width: "0%" }}
          animate={controls}
        />
      </div>

      <div className="mt-3 p-3 bg-gray-50 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-300">
        <p className="text-sm">Project examples using {tech.name}...</p>
      </div>
    </motion.div>
  );
};

export default TechStack;
