import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  const socialLinks = [
    { name: 'LinkedIn', icon: 'in', url: '#' },
    { name: 'GitHub', icon: '🐙', url: '#' },
    { name: 'X', icon: '𝕏', url: '#' },
    { name: 'Email', icon: '✉️', url: 'mailto:your.email@example.com' }
  ];

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative bg-gradient-to-b from-gray-900 to-gray-800 text-white overflow-hidden">
      <div className="container mx-auto px-4 py-16 relative z-10">
        <div className="max-w-4xl mx-auto text-center">
          {/* Large Quote */}
          <motion.h1 
            className="text-4xl md:text-6xl font-bold mb-12 leading-tight"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            "Learning, Living, and Leveling Up."
          </motion.h1>

          {/* Get in Touch Button */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="mb-16"
          >
            <a 
              href="#contact" 
              className="text-2xl font-mono text-purple-400 hover:text-purple-300 transition-colors"
            >
              GetinTouch();
            </a>
          </motion.div>

          {/* Social Icons */}
          <motion.div 
            className="flex justify-center space-x-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            {socialLinks.map((social) => (
              <a 
                key={social.name}
                href={social.url}
                target="_blank"
                rel="noopener noreferrer"
                className="w-12 h-12 rounded-full bg-gray-800 flex items-center justify-center text-xl hover:bg-purple-600 transition-colors"
                aria-label={social.name}
              >
                {social.icon}
              </a>
            ))}
          </motion.div>
        </div>

        {/* 3D Avatar - Positioned at bottom center */}
        <motion.div 
          className="absolute left-1/2 -translate-x-1/2 bottom-0 w-64 h-96"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          {/* 3D Avatar Placeholder - You can replace this with an actual 3D model */}
          <div className="w-full h-full flex items-end justify-center">
            <div className="w-48 h-48 bg-gradient-to-br from-purple-600 to-pink-600 rounded-t-full flex items-center justify-center">
              <span className="text-6xl">👨‍💻</span>
            </div>
          </div>
        </motion.div>
      </div>

      {/* Back to top link - Same as other sections */}
      <div className="fixed right-10 top-1/2 transform -translate-y-1/2 rotate-90 origin-right text-sm text-gray-400 hover:text-white transition-colors">
        <a href="#" className="flex items-center">
          <span className="mr-2">←</span> BACK TO TOP
        </a>
      </div>
    </section>
  );
};

export default Hero;
