import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import About from './components/About';
import TechStack from './components/TechStack';
import Experience from './components/Experience';
import Footer from './components/Footer';
import ArtxticCaseStudy from './components/ArtxticCaseStudy';

// Main App Layout Component
const AppLayout = () => {
  // Smooth scroll behavior for anchor links
  useEffect(() => {
    if (window.location.hash) {
      const element = document.querySelector(window.location.hash);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }, []);

  return (
    <div className="font-sans bg-paper-50 text-gray-800 min-h-screen flex flex-col">
      <Navbar />
      <main className="flex-grow">
        <section id="home" className="relative">
          <Hero />
        </section>

        <section id="about" className="relative">
          <About />
        </section>

        <section id="experience" className="relative">
          <Experience />
        </section>

        <section id="tech-stack" className="relative">
          <TechStack />
        </section>
      </main>

      <Footer />
    </div>
  );
}

// Main App Component with Routing
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<AppLayout />} />
        <Route path="/artxtic-ai" element={<ArtxticCaseStudy />} />
      </Routes>
    </Router>
  );
}

export default App;
