import React from 'react';
import { motion } from 'framer-motion';

const Experience = () => {
  const experiences = [
    {
      company: "Artxtic",
      position: "Frontend Web Developer",
      period: "2023-Present",
      description: [
        "Built an AI SaaS platform for ecommerce product image generation",
        "Implemented responsive UI components with React",
        "Optimized performance for high-volume image processing"
      ],
      projectLink: "https://artxtic.com"
    }
  ];

  return (
    <section id="experience" className="py-20 bg-gradient-to-b from-gray-900 to-gray-800 text-white">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="relative inline-block text-3xl font-bold mb-4">
            &lt;Experience/&gt;
            <span className="absolute bottom-0 left-0 w-full h-0.5 bg-gradient-to-r from-purple-500 to-cyan-400"></span>
          </h2>
        </div>

        <div className="max-w-4xl mx-auto grid gap-8">
          {experiences.map((exp, index) => (
            <motion.div 
              key={index}
              className="relative group"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
            >
              {/* Gradient Border Effect */}
              <div className="absolute -inset-1 bg-gradient-to-r from-purple-600 to-cyan-500 rounded-xl opacity-75 group-hover:opacity-100 transition-all duration-300 blur-sm group-hover:blur"></div>

              <div className="relative bg-gray-800 p-8 rounded-xl shadow-2xl">
                <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
                  <div>
                    <h3 className="text-2xl font-bold text-white mb-1">{exp.company}</h3>
                    <p className="text-purple-300 font-medium">{exp.position}</p>
                  </div>
                  <span className="text-sm text-gray-400 mt-2 md:mt-0">{exp.period}</span>
                </div>

                <ul className="mb-8 space-y-3">
                  {exp.description.map((item, i) => (
                    <li key={i} className="flex items-start">
                      <span className="text-cyan-400 mr-3 mt-1">▹</span>
                      <span className="text-gray-300">{item}</span>
                    </li>
                  ))}
                </ul>

                {exp.projectLink && (
                  <a 
                    href={exp.projectLink} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="inline-flex items-center px-6 py-2.5 bg-gradient-to-r from-purple-600 to-cyan-500 text-white font-medium rounded-lg hover:opacity-90 transition-all duration-300 group-hover:shadow-glow-cyan"
                  >
                    <span>Live View</span>
                    <svg 
                      xmlns="http://www.w3.org/2000/svg" 
                      className="h-4 w-4 ml-2 group-hover:translate-x-1 transition-transform" 
                      fill="none" 
                      viewBox="0 0 24 24" 
                      stroke="currentColor"
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
                    </svg>
                  </a>
                )}
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Back to top link */}
      <div className="fixed right-10 top-1/2 transform -translate-y-1/2 rotate-90 origin-right text-sm text-gray-400 hover:text-white transition-colors">
        <a href="#" className="flex items-center">
          <span className="mr-2">←</span> BACK TO TOP
        </a>
      </div>
    </section>
  );
};

export default Experience;
