import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  const features = [
    {
      icon: 'üåê',
      title: 'Web Development',
      description: 'I specialize in building creative web apps from scratch'
    },
    {
      icon: 'üí°',
      title: 'Ideation',
      description: 'I enjoy bringing ideas to life in the browser'
    },
    {
      icon: 'üìà',
      title: 'Continuous Growth',
      description: 'Constantly learning and leveling up my skills'
    }
  ];

  return (
    <section id="about" className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-4xl font-bold mb-4">#AboutMe</h2>
          <p className="text-xl max-w-2xl mx-auto">
            Hi! I'm Abhishek, a full stack web developer who loves creating things 
            that exist on the internet. My coding journey began in my first year of 
            college and evolved into a passion for web development.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {features.map((feature, index) => (
            <motion.div 
              key={index}
              className="bg-white p-6 rounded-lg shadow-md border border-gray-100"
              whileHover={{ y: -10, boxShadow: "0 10px 25px -5px rgba(0, 0, 0, 0.1)" }}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.3, delay: index * 0.1 }}
            >
              <div className="text-4xl mb-4 text-orange-500">{feature.icon}</div>
              <h3 className="text-xl font-bold mb-2">{feature.title}</h3>
              <p className="text-gray-700">{feature.description}</p>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default About;
